import React, { useState } from 'react';
import StatCard from './StatCard';
import './StatsSection.css';

export const StatsSection = ({ stats }) => {
    const [selectedPeriod, setSelectedPeriod] = useState('week');

    return (
        <div className="stats-container">
            <div className="stats-header">
                <h2 className="stats-title">Statistiques</h2>
                <div className="period-selector">
                    <button
                        onClick={() => setSelectedPeriod('week')}
                        className={`period-button ${selectedPeriod === 'week' ? 'active' : ''}`}
                    >
                        Semaine
                    </button>
                    <button
                        onClick={() => setSelectedPeriod('month')}
                        className={`period-button ${selectedPeriod === 'month' ? 'active' : ''}`}
                    >
                        Mois
                    </button>
                </div>
            </div>

            <div className="stats-grid">
                <StatCard
                    title="Prospects"
                    value={stats.prospects}
                    label="Prospects ajoutés"
                />
                <StatCard
                    title="Invitations"
                    value={stats.invitations}
                    label="Invitations envoyées"
                />
                <StatCard
                    title="Messages"
                    value={stats.messages}
                    label="Messages envoyés"
                />
                <StatCard
                    title="Connexions"
                    value={stats.connections}
                    label="Connexions réalisées"
                />
            </div>

            <div className="chart-container">
                <div className="chart-header">
                    <h3 className="chart-title">Évolution des prospects</h3>
                    <div className="legend">
                        <div className="legend-dot"></div>
                        <span className="legend-text">Prospects ajoutés</span>
                    </div>
                </div>
                <div className="chart">
                    <p>Graphique à venir après installation de recharts</p>
                </div>
            </div>
        </div>
    );
};

export default StatsSection;
