import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import axios from 'axios';
import './Dashboard.css';
import Settings from './Settings'
import LogoLight from '../assets/logo-light.png';
import LogoDark from '../assets/logo-dark.png';
import StatCard from './StatCard';
import ProspectListWidget from './ProspectListWidget';
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    LineController,
    CategoryScale,
    LinearScale,
    Title,
    Tooltip,
    Legend
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
    LineElement,
    PointElement,
    LineController,
    CategoryScale,
    LinearScale,
    Title,
    Tooltip,
    Legend
);

// Cr√©ation d'une instance axios configur√©e
const api = axios.create({
    baseURL: 'http://localhost:5001/api',
    headers: {
        'Content-Type': 'application/json'
    }
});

// Intercepteur pour ajouter le token automatiquement
api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

// Composants d'ic√¥nes
const NotificationIcon = () => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12 22C13.1 22 14 21.1 14 20H10C10 21.1 10.9 22 12 22ZM18 16V11C18 7.93 16.37 5.36 13.5 4.68V4C13.5 3.17 12.83 2.5 12 2.5C11.17 2.5 10.5 3.17 10.5 4V4.68C7.64 5.36 6 7.92 6 11V16L4 18V19H20V18L18 16ZM16 17H8V11C8 8.52 9.51 6.5 12 6.5C14.49 6.5 16 8.52 16 11V17Z" fill="currentColor"/>
    </svg>
);

const SettingsIcon = () => <span>‚öôÔ∏è</span>;
const RefreshIcon = () => <span>üîÑ</span>;
const LogoutIcon = () => <span>üö™</span>;

const Dashboard = () => {
    // √âtats
    const [dashboardState, setDashboardState] = useState({
        userData: {
            firstName: '',
            lastName: '',
            email: '',
            avatar: ''
        },
        loading: true,
        error: null,
        showMenu: false,
        showSettings: false,
        isDarkTheme: window.matchMedia('(prefers-color-scheme: dark)').matches,
        selectedStat: "Prospects ajout√©s",
        prospects: [],
        showProspects: true,
        showStats: true,
        notifications: []
    });

    // Refs
    const menuRef = useRef(null);
    const chartRef = useRef(null);

    // Destructuration des √©tats pour plus de lisibilit√©
    const {
        userData,
        loading,
        error,
        showMenu,
        showSettings,
        isDarkTheme,
        selectedStat,
        prospects,
        showProspects,
        showStats,
        notifications
    } = dashboardState;

    // Fonction utilitaire pour mettre √† jour l'√©tat
    const updateState = useCallback((updates) => {
        setDashboardState(prev => ({ ...prev, ...updates }));
    }, []);

    // Gestion des erreurs
    const handleError = useCallback((error, message) => {
        console.error(message, error);
        updateState({
            error: error.response?.data?.message || message,
            loading: false
        });
    }, [updateState]);

    // R√©cup√©ration des donn√©es utilisateur
    const fetchUserData = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                window.location.href = '/login';
                return;
            }

            const response = await api.get('/auth/user');
            if (response.data) {
                updateState({
                    userData: {
                        firstName: response.data.firstName || '',
                        lastName: response.data.lastName || '',
                        email: response.data.email || '',
                        avatar: response.data.avatar
                    }
                });
            }
        } catch (error) {
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                window.location.href = '/login';
            } else {
                handleError(error, 'Erreur lors de la r√©cup√©ration des donn√©es utilisateur');
            }
        }
    }, [handleError, updateState]);

    // R√©cup√©ration des prospects
    const fetchProspects = useCallback(async () => {
        try {
            const response = await api.get('/prospects');
            if (response.data.success) {
                updateState({ prospects: response.data.prospects });
            }
        } catch (error) {
            handleError(error, "Erreur lors du chargement des prospects");
        }
    }, [handleError, updateState]);

// Hooks d'effet
    useEffect(() => {
        const init = async () => {
            updateState({ loading: true });
            await Promise.all([fetchUserData(), fetchProspects()]);
            updateState({ loading: false });
        };
        init();
    }, [fetchUserData, fetchProspects, updateState]);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                updateState({ showMenu: false });
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [updateState]);

    // Gestionnaire du th√®me sombre
    useEffect(() => {
        const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
        const handleThemeChange = (e) => {
            updateState({ isDarkTheme: e.matches });
        };

        mediaQuery.addListener(handleThemeChange);
        return () => mediaQuery.removeListener(handleThemeChange);
    }, [updateState]);

    // Handlers
    const handleThemeToggle = useCallback(() => {
        updateState(prev => ({ ...prev, isDarkTheme: !prev.isDarkTheme }));
    }, [updateState]);

    const handleLogout = useCallback(() => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    }, []);

    const handleRefreshData = useCallback(async () => {
        updateState({ loading: true });
        await Promise.all([fetchUserData(), fetchProspects()]);
        updateState({ loading: false });
    }, [fetchUserData, fetchProspects, updateState]);

    const toggleMenu = useCallback(() => {
        updateState(prev => ({ ...prev, showMenu: !prev.showMenu }));
    }, [updateState]);

    const handleShowSettings = useCallback(() => {
        updateState({ showMenu: false, showSettings: true });
    }, [updateState]);

    // Donn√©es du graphique
    const chartData = useMemo(() => ({
        labels: ["Lun", "Mar", "Mer", "Jeu", "Ven", "Sam", "Dim"],
        datasets: [{
            label: selectedStat,
            data: prospects.length ? Array(7).fill(prospects.length) : Array(7).fill(0),
            borderColor: '#0077B5',
            backgroundColor: 'rgba(0, 119, 181, 0.1)',
            fill: true,
            tension: 0.4
        }]
    }), [prospects.length, selectedStat]);

    const chartOptions = useMemo(() => ({
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            title: {
                display: true,
                text: '√âvolution hebdomadaire'
            }
        },
        scales: {
            y: {
                beginAtZero: true
            }
        }
    }), []);

    // Statistiques
    const stats = useMemo(() => [
        {
            icon: "üë•",
            value: prospects.length,
            label: "Prospects ajout√©s",
            color: "#0077B5"
        },
        {
            icon: "üì®",
            value: Math.round(prospects.length * 1.5),
            label: "Invitations envoy√©es",
            color: "#00A0DC"
        },
        {
            icon: "‚úâÔ∏è",
            value: Math.round(prospects.length * 0.8),
            label: "Messages envoy√©s",
            color: "#0066FF"
        },
        {
            icon: "ü§ù",
            value: Math.round(prospects.length * 0.6),
            label: "Connexions r√©alis√©es",
            color: "#0A66C2"
        }
    ], [prospects.length]);

    if (loading) {
        return (
            <div className="loading-screen">
                <div className="loading-spinner"></div>
                <p>Chargement...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="error-screen">
                <h2>Une erreur est survenue</h2>
                <p>{error}</p>
                <button onClick={handleRefreshData} className="retry-button">
                    R√©essayer
                </button>
            </div>
        );
    }

    return (
        <div className={`dashboard-container ${isDarkTheme ? 'dark' : 'light'}`}>
            <header className="dashboard-header">
                <img src={isDarkTheme ? LogoDark : LogoLight} alt="Logo" className="dashboard-logo" />
                <div className="header-actions">
                    <button className="start-campaign-btn">
                        D√©marrer une campagne
                    </button>
                    <div className="notification-icon">
                        <span className="notification-count">{prospects.length}</span>
                        <NotificationIcon />
                    </div>
                    <div className="user-profile" onClick={toggleMenu} ref={menuRef}>
                        <img
                            src={userData.avatar}
                            alt="Avatar"
                            className="header-user-avatar"
                            onError={(e) => {
                                e.target.src = `https://ui-avatars.com/api/?name=${userData.firstName}+${userData.lastName}&background=random`;
                            }}
                        />
                        <span className="user-name">
                            {userData.firstName} {userData.lastName}
                        </span>
                        <span className="dropdown-icon">‚ñº</span>
                        {showMenu && (
                            <div className="dropdown-menu">
                                <button className="menu-item" onClick={handleShowSettings}>
                                    <SettingsIcon /> Param√®tres
                                </button>
                                <button className="menu-item" onClick={handleRefreshData}>
                                    <RefreshIcon /> Rafra√Æchir
                                </button>
                                <button className="menu-item theme-toggle" onClick={handleThemeToggle}>
                                    {isDarkTheme ? '‚òÄÔ∏è Mode clair' : 'üåô Mode sombre'}
                                </button>
                                <button className="menu-item logout" onClick={handleLogout}>
                                    <LogoutIcon /> D√©connexion
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            </header>

            <div className="dashboard-content">
                {showSettings ? (
                    <Settings onBack={() => updateState({ showSettings: false })} userData={userData} />
                ) : (
                    <>
                        {showProspects && (
                            <section className="prospects-section">
                                <h3>üë• Gestion des Prospects</h3>
                                <ProspectListWidget
                                    prospects={prospects}
                                    onProspectsUpdate={(newProspects) => updateState({ prospects: newProspects })}
                                    onRefresh={handleRefreshData}
                                />
                            </section>
                        )}

                        {showStats && (
                            <section className="statistics">
                                <h3>üìä Statistiques</h3>
                                <div className="stat-box">
                                    {stats.map((stat, index) => (
                                        <StatCard
                                            key={index}
                                            {...stat}
                                        />
                                    ))}
                                </div>

                                <div className="stat-chart">
                                    <Line
                                        data={chartData}
                                        options={chartOptions}
                                        ref={chartRef}
                                    />
                                </div>
                            </section>
                        )}

                        {/* Contr√¥le de visibilit√© des sections */}
                        <div className="sections-control">
                            <div className="control-buttons">
                                <button
                                    className={`section-toggle ${showProspects ? 'active' : ''}`}
                                    onClick={() => updateState(prev => ({ ...prev, showProspects: !prev.showProspects }))}
                                >
                                    {showProspects ? 'üîΩ' : 'üîº'} Prospects
                                </button>
                                <button
                                    className={`section-toggle ${showStats ? 'active' : ''}`}
                                    onClick={() => updateState(prev => ({ ...prev, showStats: !prev.showStats }))}
                                >
                                    {showStats ? 'üîΩ' : 'üîº'} Statistiques
                                </button>
                            </div>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
