import React, { useState } from 'react';
import { Line } from 'recharts';
import { ArrowUpRight, ArrowDownRight, TrendingUp } from 'lucide-react';

const StatCard = ({ title, value, trend, percentage, icon: Icon }) => {
    const isPositive = trend === 'up';

    return (
        <div className="flex flex-col p-6 bg-white rounded-xl shadow-sm hover:shadow-md transition-all cursor-pointer">
            <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-2">
                    <Icon className="w-5 h-5 text-gray-400" />
                    <span className="text-sm font-medium text-gray-500">{title}</span>
                </div>
                <div className={`flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                    isPositive ? 'text-green-500 bg-green-50' : 'text-red-500 bg-red-50'
                }`}>
                    {isPositive ? <ArrowUpRight className="w-3 h-3 mr-1" /> : <ArrowDownRight className="w-3 h-3 mr-1" />}
                    {percentage}%
                </div>
            </div>
            <div className="flex items-baseline">
                <span className="text-2xl font-semibold">{value}</span>
                <span className="ml-2 text-sm text-gray-500">depuis le mois dernier</span>
            </div>
        </div>
    );
};

const StatsSection = ({ stats }) => {
    const [selectedPeriod, setSelectedPeriod] = useState('week');

    const data = [
        { name: 'Lun', value: stats.prospects },
        { name: 'Mar', value: stats.prospects * 1.2 },
        { name: 'Mer', value: stats.prospects * 0.8 },
        { name: 'Jeu', value: stats.prospects * 1.5 },
        { name: 'Ven', value: stats.prospects * 1.3 },
        { name: 'Sam', value: stats.prospects * 0.9 },
        { name: 'Dim', value: stats.prospects },
    ];

    return (
        <div className="space-y-6 p-6 bg-gray-50 rounded-2xl">
            <div className="flex items-center justify-between">
                <h2 className="text-xl font-semibold text-gray-800">Statistiques</h2>
                <div className="flex space-x-2">
                    <button
                        onClick={() => setSelectedPeriod('week')}
                        className={`px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
                            selectedPeriod === 'week'
                                ? 'bg-blue-600 text-white'
                                : 'bg-white text-gray-600 hover:bg-gray-50'
                        }`}
                    >
                        Semaine
                    </button>
                    <button
                        onClick={() => setSelectedPeriod('month')}
                        className={`px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
                            selectedPeriod === 'month'
                                ? 'bg-blue-600 text-white'
                                : 'bg-white text-gray-600 hover:bg-gray-50'
                        }`}
                    >
                        Mois
                    </button>
                </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard
                    title="Prospects ajoutés"
                    value={stats.prospects}
                    trend="up"
                    percentage="12"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Invitations envoyées"
                    value={stats.invitations}
                    trend="up"
                    percentage="8"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Messages envoyés"
                    value={stats.messages}
                    trend="down"
                    percentage="3"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Connexions réalisées"
                    value={stats.connections}
                    trend="up"
                    percentage="24"
                    icon={TrendingUp}
                />
            </div>

            <div className="bg-white p-6 rounded-xl shadow-sm">
                <div className="flex items-center justify-between mb-6">
                    <h3 className="text-lg font-medium text-gray-800">Évolution des prospects</h3>
                    <div className="flex items-center space-x-4">
                        <div className="flex items-center">
                            <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
                            <span className="text-sm text-gray-500">Prospects ajoutés</span>
                        </div>
                    </div>
                </div>
                <div className="h-64">
                    <LineChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Line
                            type="monotone"
                            dataKey="value"
                            stroke="#3B82F6"
                            strokeWidth={2}
                            dot={{ r: 4 }}
                            activeDot={{ r: 8 }}
                        />
                    </LineChart>
                </div>
            </div>
        </div>
    );
};

export default StatsSection;
