import React, { useState } from 'react';
import { ArrowUpRight, ArrowDownRight, TrendingUp } from 'lucide-react';
import './StatsSection.css';

const StatCard = ({ title, value, trend, percentage, icon: Icon }) => {
    const isPositive = trend === 'up';

    return (
        <div className="stat-card">
            <div className="stat-card-header">
                <div className="stat-title">
                    <Icon size={20} />
                    <span>{title}</span>
                </div>
                <div className={`trend ${isPositive ? 'trend-up' : 'trend-down'}`}>
                    {isPositive ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
                    {percentage}%
                </div>
            </div>
            <div className="stat-value-container">
                <span className="stat-value">{value}</span>
                <span className="stat-period">depuis le mois dernier</span>
            </div>
        </div>
    );
};

export const StatsSection = ({ stats }) => {
    const [selectedPeriod, setSelectedPeriod] = useState('week');

    return (
        <div className="stats-container">
            <div className="stats-header">
                <h2 className="stats-title">Statistiques</h2>
                <div className="period-selector">
                    <button
                        onClick={() => setSelectedPeriod('week')}
                        className={`period-button ${selectedPeriod === 'week' ? 'active' : ''}`}
                    >
                        Semaine
                    </button>
                    <button
                        onClick={() => setSelectedPeriod('month')}
                        className={`period-button ${selectedPeriod === 'month' ? 'active' : ''}`}
                    >
                        Mois
                    </button>
                </div>
            </div>

            <div className="stats-grid">
                <StatCard
                    title="Prospects ajoutés"
                    value={stats.prospects}
                    trend="up"
                    percentage="12"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Invitations envoyées"
                    value={stats.invitations}
                    trend="up"
                    percentage="8"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Messages envoyés"
                    value={stats.messages}
                    trend="down"
                    percentage="3"
                    icon={TrendingUp}
                />
                <StatCard
                    title="Connexions réalisées"
                    value={stats.connections}
                    trend="up"
                    percentage="24"
                    icon={TrendingUp}
                />
            </div>

            <div className="chart-container">
                <div className="chart-header">
                    <h3 className="chart-title">Évolution des prospects</h3>
                    <div className="legend">
                        <div className="legend-dot"></div>
                        <span className="legend-text">Prospects ajoutés</span>
                    </div>
                </div>
                <div className="chart">
                    {/* Le graphique sera ajouté ici après installation de recharts */}
                    <p className="chart-placeholder">Graphique en cours de chargement...</p>
                </div>
            </div>
        </div>
    );
};

export default StatsSection;
