import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const AnimatedBackground = () => {
    const mountRef = useRef(null);

    useEffect(() => {
        // Initialisation de la scène
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setClearColor(0x000000, 0); // Fond transparent
        mountRef.current.appendChild(renderer.domElement);

        // Ajout d'une sphère (la planète)
        const sphereGeometry = new THREE.SphereGeometry(5, 32, 32);
        const sphereMaterial = new THREE.MeshBasicMaterial({
            wireframe: true,
            color: 0xff4d4d,
        });
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Ajout de points connectés autour de la sphère
        const pointsMaterial = new THREE.PointsMaterial({ color: 0xff4d4d, size: 0.1 });
        const pointsGeometry = new THREE.BufferGeometry();
        const points = [];

        for (let i = 0; i < 100; i++) {
            const x = (Math.random() - 0.5) * 20;
            const y = (Math.random() - 0.5) * 20;
            const z = (Math.random() - 0.5) * 20;
            points.push(x, y, z);
        }

        pointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(points, 3));
        const pointsMesh = new THREE.Points(pointsGeometry, pointsMaterial);
        scene.add(pointsMesh);

        // Animation
        camera.position.z = 15;

        const animate = () => {
            requestAnimationFrame(animate);
            sphere.rotation.y += 0.002; // Rotation de la sphère
            pointsMesh.rotation.y -= 0.001; // Rotation des points
            renderer.render(scene, camera);
        };

        animate();

        // Nettoyage
        return () => {
            renderer.dispose();
            mountRef.current.removeChild(renderer.domElement);
        };
    }, []);

    return <div ref={mountRef} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', zIndex: -1 }} />;
};

export default AnimatedBackground;
