import React, { useState } from 'react';
import CSVReader from 'react-csv-reader';
import axios from 'axios';
import './ProspectListWidget.css';

const ProspectListWidget = ({ prospects, onProspectsUpdate }) => {
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportSection, setShowImportSection] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleFileUpload = async (data) => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            const formattedProspects = data.map(row => ({
                prenom: row[0] || 'Non disponible',
                nom: row[1] || 'Non disponible',
                email: row[2] || 'Non disponible',
                societe: row[3] || 'Non disponible',
                linkedin: row[4] || 'Non disponible'
            }));

            for (const prospect of formattedProspects) {
                await axios.post('http://localhost:5001/api/prospects/add', prospect, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setError('');
                setShowImportSection(false);
            }
        } catch (error) {
            console.error("Erreur lors de l'import des prospects:", error);
            setError("Erreur lors de l'import des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteSelected = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            for (const id of selectedProspects) {
                await axios.delete(`http://localhost:5001/api/prospects/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setSelectedProspects([]);
                setError('');
            }
        } catch (error) {
            console.error("Erreur lors de la suppression des prospects:", error);
            setError("Erreur lors de la suppression des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleSelectProspect = (id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(prospectId => prospectId !== id) : [...prev, id]
        );
    };

    const handleSelectAll = () => {
        if (selectedProspects.length === prospects.length) {
            setSelectedProspects([]);
        } else {
            setSelectedProspects(prospects.map(prospect => prospect._id));
        }
    };

    return (
        <div className="prospect-list-widget bg-white rounded-lg shadow-lg p-6">
            {/* En-tête */}
            <div className="flex justify-between items-center pb-4 border-b">
                <div>
                    <h2 className="text-xl font-semibold text-gray-800">Gestion des Prospects</h2>
                    <p className="text-gray-500">Voir toutes les informations de vos prospects.</p>
                </div>
                <div className="flex space-x-3">
                    <button
                        onClick={() => setShowImportSection(!showImportSection)}
                        className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
                    >
                        Importer
                    </button>
                    <button
                        className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
                    >
                        + Ajouter
                    </button>
                </div>
            </div>

            {/* Section Import */}
            {showImportSection && (
                <div className="import-section-dropdown my-4">
                    <CSVReader
                        onFileLoaded={handleFileUpload}
                        cssClass="csv-reader-input"
                        label="Importer une liste CSV"
                        parserOptions={{ header: false }}
                    />
                    {error && <p className="error-message text-red-600">{error}</p>}
                </div>
            )}

            {/* Table des prospects */}
            <div className={`prospect-content ${showImportSection ? 'dimmed' : ''}`}>
                {loading ? (
                    <div className="loading">Chargement...</div>
                ) : (
                    <div className="prospect-table-container overflow-auto">
                        <table className="w-full text-left border-collapse">
                            <thead>
                            <tr className="bg-gray-50">
                                <th className="px-4 py-3">
                                    <input
                                        type="checkbox"
                                        onChange={handleSelectAll}
                                        checked={selectedProspects.length === prospects.length}
                                    />
                                </th>
                                <th className="px-4 py-3 text-sm font-medium text-gray-700">Client</th>
                                <th className="px-4 py-3 text-sm font-medium text-gray-700">Email</th>
                                <th className="px-4 py-3 text-sm font-medium text-gray-700">Société</th>
                                <th className="px-4 py-3 text-sm font-medium text-gray-700">LinkedIn</th>
                            </tr>
                            </thead>
                            <tbody>
                            {prospects.map((prospect) => (
                                <tr key={prospect._id} className="hover:bg-gray-100">
                                    <td className="px-4 py-3">
                                        <input
                                            type="checkbox"
                                            checked={selectedProspects.includes(prospect._id)}
                                            onChange={() => handleSelectProspect(prospect._id)}
                                        />
                                    </td>
                                    <td className="px-4 py-3 flex items-center space-x-3">
                                        <img
                                            src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                            alt={`${prospect.prenom} ${prospect.nom}`}
                                            className="w-8 h-8 rounded-full"
                                        />
                                        <div>
                                            <div className="text-sm font-medium text-gray-800">
                                                {prospect.prenom} {prospect.nom}
                                            </div>
                                            <div className="text-sm text-gray-500">ID: {prospect._id.slice(-4)}</div>
                                        </div>
                                    </td>
                                    <td className="px-4 py-3 text-sm text-gray-600">
                                        {prospect.email || 'Non disponible'}
                                    </td>
                                    <td className="px-4 py-3 text-sm text-gray-600">
                                        {prospect.societe || 'Non disponible'}
                                    </td>
                                    <td className="px-4 py-3 text-sm text-blue-600">
                                        <a href={prospect.linkedin} target="_blank" rel="noopener noreferrer">
                                            Voir profil
                                        </a>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>

            {/* Pagination */}
            <div className="flex items-center justify-between py-3 border-t border-gray-200">
                <span className="text-sm text-gray-600">Affichage de 1 à {prospects.length} sur {prospects.length} résultats</span>
                <div className="flex items-center space-x-2">
                    <button className="px-3 py-1 text-sm font-medium text-gray-600 bg-gray-200 rounded hover:bg-gray-300">Précédent</button>
                    <button className="px-3 py-1 text-sm font-medium text-gray-600 bg-gray-200 rounded hover:bg-gray-300">Suivant</button>
                </div>
            </div>
        </div>
    );
};

export default ProspectListWidget;
