import React, { useState, useEffect } from 'react';
import CSVReader from 'react-csv-reader';
import axios from 'axios';
import './ProspectListWidget.css';

const ProspectListWidget = ({ prospects, onProspectsUpdate }) => {
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportModal, setShowImportModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [displayedProspects, setDisplayedProspects] = useState([]);
    const [itemsPerPage] = useState(5);

    // État pour gérer les listes de prospects
    const [lists, setLists] = useState([]);
    const [selectedList, setSelectedList] = useState(null);
    const [newListName, setNewListName] = useState('');
    const [listSearchTerm, setListSearchTerm] = useState('');

    // Mise à jour des prospects affichés lors du changement de recherche
    useEffect(() => {
        const filteredProspects = prospects.filter(prospect =>
            prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setDisplayedProspects(filteredProspects.slice(0, itemsPerPage));
    }, [searchTerm, prospects, itemsPerPage]);

    useEffect(() => {
        // Charger les listes de prospects
        const fetchLists = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5001/api/lists', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setLists(response.data.lists);
                setSelectedList(null);
            } catch (error) {
                console.error("Erreur lors du chargement des listes de prospects:", error);
            }
        };
        fetchLists();
    }, []);

    useEffect(() => {
        const fetchProspectsByList = async () => {
            try {
                const token = localStorage.getItem('token');
                let response;
                if (selectedList) {
                    response = await axios.get(`http://localhost:5001/api/prospects/${selectedList}`, {
                        headers: { Authorization: `Bearer ${token}` }
                    });
                } else {
                    response = await axios.get('http://localhost:5001/api/prospects', {
                        headers: { Authorization: `Bearer ${token}` }
                    });
                }
                onProspectsUpdate(response.data.prospects);

                // Filtrer les prospects en fonction du terme de recherche actuel
                const filteredProspects = response.data.prospects.filter(prospect =>
                    prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    prospect.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
                );
                setDisplayedProspects(filteredProspects.slice(0, itemsPerPage));
            } catch (error) {
                console.error("Erreur lors du chargement des prospects par liste:", error);
            }
        };
        fetchProspectsByList();
    }, [selectedList, searchTerm]);

    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + window.scrollY >= document.documentElement.scrollHeight - 50) {
                loadMoreProspects();
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [displayedProspects, searchTerm]);

    const loadMoreProspects = () => {
        const filteredProspects = prospects.filter(prospect =>
            prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
        );
        const startIndex = displayedProspects.length;
        const newProspects = filteredProspects.slice(startIndex, startIndex + itemsPerPage);
        if (newProspects.length > 0) {
            setDisplayedProspects(prev => [...prev, ...newProspects]);
        }
    };

    const handleFileUpload = async (data) => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            const formattedProspects = data.map(row => ({
                prenom: row[0] || 'Non disponible',
                nom: row[1] || 'Non disponible',
                email: row[2] || 'Non disponible',
                societe: row[3] || 'Non disponible',
                linkedin: row[4] || 'Non disponible'
            }));

            for (const prospect of formattedProspects) {
                await axios.post('http://localhost:5001/api/prospects/add', prospect, {
                    headers: { Authorization: `Bearer ${token}` }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setError('');
                setShowImportModal(false);
            }
        } catch (error) {
            console.error("Erreur lors de l'import des prospects:", error);
            setError("Erreur lors de l'import des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteSelected = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');

            for (const id of selectedProspects) {
                await axios.delete(`http://localhost:5001/api/prospects/${id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setSelectedProspects([]);
                setError('');
            }
        } catch (error) {
            console.error("Erreur lors de la suppression des prospects:", error);
            setError("Erreur lors de la suppression des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleSelectProspect = (id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(prospectId => prospectId !== id) : [...prev, id]
        );
    };

    const handleSelectAll = () => {
        const currentProspectIds = displayedProspects.map(prospect => prospect._id);
        if (currentProspectIds.every(id => selectedProspects.includes(id))) {
            setSelectedProspects(prev => prev.filter(id => !currentProspectIds.includes(id)));
        } else {
            setSelectedProspects(prev => [...new Set([...prev, ...currentProspectIds])]);
        }
    };

    // Filtrer les listes en fonction du terme de recherche
    const filteredLists = lists.filter(list =>
        list.name.toLowerCase().includes(listSearchTerm.toLowerCase())
    );

    // Créer une nouvelle liste
    const createNewList = async () => {
        if (newListName.trim() === '') return;
        try {
            const token = localStorage.getItem('token');
            const response = await axios.post('http://localhost:5001/api/lists', { name: newListName }, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setLists([...lists, response.data.list]);
            setNewListName('');
        } catch (error) {
            console.error("Erreur lors de la création d'une nouvelle liste:", error);
        }
    };

    return (
        <div className="prospect-list-widget">
            <div className="list-sidebar">
                <h3>Listes de Prospects</h3>
                <input
                    type="text"
                    placeholder="Rechercher une liste..."
                    value={listSearchTerm}
                    onChange={(e) => setListSearchTerm(e.target.value)}
                    className="list-search-input"
                />
                <ul className="list-names">
                    <li onClick={() => setSelectedList(null)} className={!selectedList ? 'active' : ''}>
                        Tous les prospects
                    </li>
                    {filteredLists.map(list => (
                        <li
                            key={list._id}
                            onClick={() => setSelectedList(list._id)}
                            className={selectedList === list._id ? 'active' : ''}
                        >
                            {list.name}
                        </li>
                    ))}
                </ul>
                <input
                    type="text"
                    placeholder="Nom de la nouvelle liste"
                    value={newListName}
                    onChange={(e) => setNewListName(e.target.value)}
                    className="new-list-input"
                />
                <button onClick={createNewList} className="btn add-list-btn">Créer une liste</button>
            </div>

            <div className="main-content">
                <div className="prospect-actions">
                    <button onClick={() => setShowImportModal(true)} className="btn import-btn">
                        Importer des prospects
                    </button>
                    <button
                        onClick={handleDeleteSelected}
                        className={`btn delete-btn ${selectedProspects.length > 0 ? 'enabled' : 'disabled'}`}
                        disabled={loading || selectedProspects.length === 0}
                    >
                        Supprimer les prospects sélectionnés
                    </button>
                    <button onClick={handleSelectAll} className="select-all-btn">
                        {displayedProspects.every(prospect => selectedProspects.includes(prospect._id))
                            ? "Tout désélectionner"
                            : "Sélectionner tout"
                        }
                    </button>
                    <div className="search-container">
                        <input
                            type="text"
                            className="search-input"
                            placeholder="Rechercher un prospect..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        {searchTerm && (
                            <button
                                className="clear-search"
                                onClick={() => setSearchTerm('')}
                            >
                                ✕
                            </button>
                        )}
                    </div>
                </div>

                <div className={`prospect-content ${showImportModal ? 'dimmed' : ''}`}>
                    {loading ? (
                        <div className="loading">Chargement...</div>
                    ) : (
                        <div className="prospect-table-container">
                            <table className="prospect-table">
                                <thead>
                                <tr>
                                    <th>Sélection</th>
                                    <th>Photo</th>
                                    <th>Nom</th>
                                    <th>Email</th>
                                    <th>Société</th>
                                    <th>LinkedIn</th>
                                </tr>
                                </thead>
                                <tbody>
                                {displayedProspects.length === 0 ? (
                                    <tr>
                                        <td colSpan="6" className="no-results">
                                            Aucun prospect trouvé
                                        </td>
                                    </tr>
                                ) : (
                                    displayedProspects.map((prospect) => (
                                        <tr key={prospect._id}>
                                            <td>
                                                <input
                                                    type="checkbox"
                                                    checked={selectedProspects.includes(prospect._id)}
                                                    onChange={() => handleSelectProspect(prospect._id)}
                                                />
                                            </td>
                                            <td>
                                                <img
                                                    src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                                    alt={`${prospect.prenom} ${prospect.nom}`}
                                                    className="prospect-avatar"
                                                />
                                            </td>
                                            <td>{prospect.prenom} {prospect.nom}</td>
                                            <td>{prospect.email || 'Non disponible'}</td>
                                            <td>{prospect.societe || 'Non disponible'}</td>
                                            <td>
                                                <a
                                                    href={prospect.linkedin}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    className="linkedin-btn"
                                                >
                                                    profil
                                                </a>
                                            </td>
                                        </tr>
                                    ))
                                )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {showImportModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Importer des prospects</h3>
                        <CSVReader
                            onFileLoaded={handleFileUpload}
                            cssClass="csv-reader-input"
                            label="Importer une liste CSV"
                            parserOptions={{ header: false }}
                        />
                        <button onClick={() => setShowImportModal(false)} className="btn close-btn">
                            Fermer
                        </button>
                        {error && <p className="error-message">{error}</p>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProspectListWidget;
