import React, { useState } from 'react';
import CSVReader from 'react-csv-reader';
import axios from 'axios';
import './ProspectListWidget.css';

const ProspectListWidget = ({ prospects, onProspectsUpdate }) => {
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportModal, setShowImportModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleFileUpload = async (data) => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const formattedProspects = data.map(row => ({
                prenom: row[0] || 'Non disponible',
                nom: row[1] || 'Non disponible',
                email: row[2] || 'Non disponible',
                societe: row[3] || 'Non disponible',
                linkedin: row[4] || 'Non disponible'
            }));

            for (const prospect of formattedProspects) {
                await axios.post('http://localhost:5001/api/prospects/add', prospect, {
                    headers: { Authorization: `Bearer ${token}` }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setError('');
                setShowImportModal(false);
            }
        } catch (error) {
            console.error("Erreur lors de l'import des prospects:", error);
            setError("Erreur lors de l'import des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteSelected = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            for (const id of selectedProspects) {
                await axios.delete(`http://localhost:5001/api/prospects/${id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
            }

            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.data.success) {
                onProspectsUpdate(response.data.prospects);
                setSelectedProspects([]);
                setError('');
            }
        } catch (error) {
            console.error("Erreur lors de la suppression des prospects:", error);
            setError("Erreur lors de la suppression des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleSelectProspect = (id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(prospectId => prospectId !== id) : [...prev, id]
        );
    };

    const filteredProspects = prospects.filter(prospect =>
        `${prospect.prenom} ${prospect.nom}`.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="prospect-list-widget">
            <div className="prospect-actions">
                <input
                    type="text"
                    placeholder="Rechercher un prospect"
                    className="search-input"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button onClick={() => setShowImportModal(true)} className="import-btn">
                    Importer des prospects
                </button>
                <button
                    onClick={handleDeleteSelected}
                    className={`delete-btn ${selectedProspects.length > 0 ? 'enabled' : 'disabled'}`}
                    disabled={loading || selectedProspects.length === 0}
                >
                    Supprimer les prospects sélectionnés
                </button>
            </div>

            {showImportModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Importer une liste de prospects</h3>
                        <CSVReader onFileLoaded={handleFileUpload} cssClass="csv-reader-input" />
                        {error && <p className="error-message">{error}</p>}
                        <button onClick={() => setShowImportModal(false)} className="close-btn">Fermer</button>
                    </div>
                </div>
            )}

            <div className="prospect-table-container">
                <table className="prospect-table">
                    <thead>
                    <tr>
                        <th>Photo</th>
                        <th>Nom</th>
                        <th>Email</th>
                        <th>Société</th>
                        <th>LinkedIn</th>
                    </tr>
                    </thead>
                    <tbody>
                    {filteredProspects.map(prospect => (
                        <tr key={prospect._id}>
                            <td>
                                <img
                                    src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                    alt={`${prospect.prenom} ${prospect.nom}`}
                                    className="prospect-avatar"
                                />
                            </td>
                            <td>{prospect.prenom} {prospect.nom}</td>
                            <td>{prospect.email || 'Non disponible'}</td>
                            <td>{prospect.societe || 'Non disponible'}</td>
                            <td>
                                <a href={prospect.linkedin} target="_blank" rel="noopener noreferrer" className="linkedin-btn">
                                    Voir le profil
                                </a>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            <div className="pagination">
                <button disabled>Précédent</button>
                <span>Page 1</span>
                <button>Suivant</button>
            </div>
        </div>
    );
};

export default ProspectListWidget;
