// Dashboard.js
    import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';
import './Dashboard.css';
import Settings from './Settings';
import LogoLight from '../assets/logo-light.png';
import LogoDark from '../assets/logo-dark.png';
import StatCard from './StatCard';
import ProspectListWidget from './ProspectListWidget';
import NotificationIcon from './icons/NotificationIcon';
import SettingsIcon from './icons/SettingsIcon';
import RefreshIcon from './icons/RefreshIcon';
import LogoutIcon from './icons/LogoutIcon';
import { useNavigate } from 'react-router-dom';

ChartJS.register(LineElement, PointElement, CategoryScale, LinearScale, Tooltip, Legend);

// Axios instance with token
const api = axios.create({
    baseURL: 'http://localhost:5001/api',
    headers: { 'Content-Type': 'application/json' }
});

api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) config.headers.Authorization = `Bearer ${token}`;
    return config;
});

const Dashboard = () => {
    const [state, setState] = useState({
        userData: {},
        loading: true,
        error: null,
        showMenu: false,
        showSettings: false,
        isDarkTheme: window.matchMedia('(prefers-color-scheme: dark)').matches,
        selectedStat: "Prospects ajoutés",
        prospects: [],
        showProspects: true,
        showStats: true
    });

    const menuRef = useRef(null);
    const navigate = useNavigate();

    const { userData, loading, error, showMenu, showSettings, isDarkTheme, selectedStat, prospects, showProspects, showStats } = state;

    const updateState = (updates) => setState(prev => ({ ...prev, ...updates }));

    // Fetch user data and prospects
    const fetchUserData = useCallback(async () => {
        try {
            const response = await api.get('/auth/user');
            if (response.data) {
                updateState({
                    userData: response.data,
                    error: null
                });
            }
        } catch (err) {
            if (err.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/login');
            } else {
                updateState({ error: 'Erreur lors de la récupération des données utilisateur', loading: false });
            }
        }
    }, [navigate]);

    const fetchProspects = useCallback(async () => {
        try {
            const response = await api.get('/prospects');
            if (response.data.success) {
                updateState({ prospects: response.data.prospects });
            }
        } catch {
            updateState({ error: 'Erreur lors du chargement des prospects' });
        }
    }, []);

    // Initial data loading
    useEffect(() => {
        updateState({ loading: true });
        Promise.all([fetchUserData(), fetchProspects()]).then(() => updateState({ loading: false }));
    }, [fetchUserData, fetchProspects]);

    // Theme and click-outside detection
    useEffect(() => {
        const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
        mediaQuery.addListener(e => updateState({ isDarkTheme: e.matches }));
        return () => mediaQuery.removeListener(e => updateState({ isDarkTheme: e.matches }));
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                updateState({ showMenu: false });
            }
        };
        document.addEventListener('click', handleClickOutside);
        return () => document.removeEventListener('click', handleClickOutside);
    }, []);

    const chartData = useMemo(() => ({
        labels: ["Lun", "Mar", "Mer", "Jeu", "Ven", "Sam", "Dim"],
        datasets: [{
            label: selectedStat,
            data: Array(7).fill(prospects.length),
            borderColor: '#0077B5',
            backgroundColor: 'rgba(0, 119, 181, 0.1)',
            fill: true,
            tension: 0.4
        }]
    }), [prospects.length, selectedStat]);

    const chartOptions = useMemo(() => ({
        responsive: true,
        plugins: {
            legend: { position: 'top' },
            title: { display: true, text: 'Évolution hebdomadaire' }
        },
        scales: { y: { beginAtZero: true } }
    }), []);

    const stats = useMemo(() => [
        { icon: "👥", value: prospects.length, label: "Prospects ajoutés", color: "#0077B5" },
        { icon: "📨", value: Math.round(prospects.length * 1.5), label: "Invitations envoyées", color: "#00A0DC" },
        { icon: "✉️", value: Math.round(prospects.length * 0.8), label: "Messages envoyés", color: "#0066FF" },
        { icon: "🤝", value: Math.round(prospects.length * 0.6), label: "Connexions réalisées", color: "#0A66C2" }
    ], [prospects.length]);

    // Handlers
    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    const handleRefreshData = () => {
        updateState({ loading: true });
        Promise.all([fetchUserData(), fetchProspects()]).then(() => updateState({ loading: false }));
    };

    const toggleTheme = () => updateState(prev => ({ isDarkTheme: !prev.isDarkTheme }));
    const toggleSection = (section) => updateState(prev => ({ [section]: !prev[section] }));

    if (loading) {
        return <div className="loading-screen"><p>Chargement...</p></div>;
    }

    if (error) {
        return <div className="error-screen"><p>{error}</p><button onClick={handleRefreshData}>Réessayer</button></div>;
    }

    return (
        <div className={`dashboard-container ${isDarkTheme ? 'dark' : 'light'}`}>
            <header className="dashboard-header">
                <img src={isDarkTheme ? LogoDark : LogoLight} alt="Logo" className="dashboard-logo" />
                <div className="header-actions">
                    <button className="start-campaign-btn">Démarrer une campagne</button>
                    <NotificationIcon count={prospects.length} />
                    <div className="user-profile" ref={menuRef} onClick={() => updateState({ showMenu: !showMenu })}>
                        <img src={userData.avatar || `https://ui-avatars.com/api/?name=${userData.firstName}+${userData.lastName}&background=random`} alt="Avatar" className="header-user-avatar" />
                        <span className="user-name">{userData.firstName} {userData.lastName}</span>
                        <span className="dropdown-icon">▼</span>
                        {showMenu && (
                            <div className="dropdown-menu">
                                <button onClick={() => updateState({ showSettings: true })}><SettingsIcon /> Paramètres</button>
                                <button onClick={handleRefreshData}><RefreshIcon /> Rafraîchir</button>
                                <button onClick={toggleTheme}>{isDarkTheme ? '☀️ Mode clair' : '🌙 Mode sombre'}</button>
                                <button onClick={handleLogout}><LogoutIcon /> Déconnexion</button>
                            </div>
                        )}
                    </div>
                </div>
            </header>

            <div className="dashboard-content">
                {showSettings ? (
                    <Settings onBack={() => updateState({ showSettings: false })} userData={userData} />
                ) : (
                    <>
                        {showProspects && (
                            <section className="prospects-section">
                                <h3>👥 Gestion des Prospects</h3>
                                <ProspectListWidget prospects={prospects} onProspectsUpdate={(newProspects) => updateState({ prospects: newProspects })} />
                            </section>
                        )}

                        {showStats && (
                            <section className="statistics">
                                <h3>📊 Statistiques</h3>
                                <div className="stat-box">
                                    {stats.map((stat, index) => <StatCard key={index} {...stat} />)}
                                </div>
                                <div className="stat-chart"><Line data={chartData} options={chartOptions} /></div>
                            </section>
                        )}

                        <div className="sections-control">
                            <button onClick={() => toggleSection('showProspects')}>{showProspects ? '🔽' : '🔼'} Prospects</button>
                            <button onClick={() => toggleSection('showStats')}>{showStats ? '🔽' : '🔼'} Statistiques</button>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
