import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    LineController,
    CategoryScale,
    LinearScale,
    Title,
    Tooltip,
    Legend
} from 'chart.js';

ChartJS.register(LineElement, PointElement, LineController, CategoryScale, LinearScale, Title, Tooltip, Legend);

const StatCard = ({ icon, value, label, color }) => (
    <div className="bg-white p-6 rounded-lg shadow-sm border-l-4 transition-transform hover:scale-[1.02]" style={{ borderLeftColor: color }}>
        <div className="text-2xl mb-3">{icon}</div>
        <div className="text-2xl font-bold mb-1" style={{ color }}>{value}</div>
        <div className="text-gray-600 text-sm">{label}</div>
    </div>
);

const NotificationIcon = () => (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-gray-400">
        <path d="M18 8A6 6 0 0 0 6 8c0 7-3 9-3 9h18s-3-2-3-9"></path>
        <path d="M13.73 21a2 2 0 0 1-3.46 0"></path>
    </svg>
);

const Dashboard = () => {
    const [userData, setUserData] = useState({ firstName: '', lastName: '', email: '', avatar: '' });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showMenu, setShowMenu] = useState(false);
    const [showSettings, setShowSettings] = useState(false);
    const [prospects, setProspects] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportModal, setShowImportModal] = useState(false);
    const menuRef = useRef(null);

    const [visibleWidgets, setVisibleWidgets] = useState({
        statistics: true,
        prospects: true,
        messages: false,
        campaigns: false
    });

    const handleError = useCallback((error) => {
        if (error.response?.status === 401) {
            localStorage.removeItem('token');
            window.location.href = '/login';
        }
        setError('Une erreur est survenue');
        setLoading(false);
    }, []);

    const fetchUserData = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                window.location.href = '/login';
                return;
            }
            const response = await axios.get('http://localhost:5001/api/auth/user', {
                headers: { Authorization: `Bearer ${token}` }
            });
            if (response.data) {
                setUserData({
                    firstName: response.data.firstName || '',
                    lastName: response.data.lastName || '',
                    email: response.data.email || '',
                    avatar: response.data.avatar
                });
            }
        } catch (error) {
            handleError(error);
        }
    }, [handleError]);

    const fetchProspects = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5001/api/prospects', {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            if (response.data.success) {
                setProspects(response.data.prospects);
            }
        } catch (error) {
            handleError(error);
        } finally {
            setLoading(false);
        }
    }, [handleError]);

    useEffect(() => {
        fetchUserData();
        fetchProspects();
    }, [fetchUserData, fetchProspects]);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setShowMenu(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const chartData = {
        labels: ["Lun", "Mar", "Mer", "Jeu", "Ven", "Sam", "Dim"],
        datasets: [{
            label: "Prospects ajoutÃ©s",
            data: prospects.length ? Array(7).fill(prospects.length) : Array(7).fill(0),
            borderColor: '#7C3AED',
            backgroundColor: 'rgba(124, 58, 237, 0.1)',
            fill: true,
            tension: 0.4
        }]
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    };

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleSelectProspect = (id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(p => p !== id) : [...prev, id]
        );
    };

    const handleImport = async (file) => {
        // Logique d'import
        setShowImportModal(false);
    };

    const filteredProspects = prospects.filter(prospect =>
        prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) return (
        <div className="min-h-screen flex items-center justify-center bg-[#F9FAFB]">
            <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-[#7C3AED] rounded-full animate-bounce" style={{ animationDelay: '0s' }}></div>
                <div className="w-4 h-4 bg-[#7C3AED] rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-4 h-4 bg-[#7C3AED] rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
            </div>
        </div>
    );

    if (error) return (
        <div className="min-h-screen flex items-center justify-center bg-[#F9FAFB]">
            <div className="text-red-600 flex items-center space-x-2">
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                </svg>
                <span>{error}</span>
            </div>
        </div>
    );

    return (
        <div className="min-h-screen bg-[#F9FAFB]">
            {/* Header */}
            <header className="fixed top-0 w-full bg-[#14082E] shadow-sm z-50">
                <div className="max-w-[1800px] mx-auto px-6 py-4 flex justify-between items-center">
                    {/* Section gauche avec logo et recherche */}
                    <div className="flex items-center gap-8">
                        <h1 className="text-xl font-semibold text-white">Dashboard</h1>

                        {/* Barre de recherche */}
                        <div className="relative">
                            <input
                                type="text"
                                placeholder="Search for clients, vehicles and more"
                                className="w-[400px] bg-[#2D1F43] text-gray-200 placeholder-gray-400 text-sm rounded-lg pl-10 pr-4 py-2.5 border border-[#3D2F53] focus:outline-none focus:border-[#6D28D9] transition-colors"
                            />
                            <svg
                                className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400"
                                width="16"
                                height="16"
                                viewBox="0 0 24 24"
                                fill="none"
                                stroke="currentColor"
                                strokeWidth="2"
                                strokeLinecap="round"
                                strokeLinejoin="round"
                            >
                                <circle cx="11" cy="11" r="8" />
                                <path d="M21 21l-4.35-4.35" />
                            </svg>
                        </div>
                    </div>

                    {/* Section droite avec actions */}
                    <div className="flex items-center gap-6">
                        {/* Bouton DÃ©marrer une campagne */}
                        <button className="px-4 py-2 bg-[#7C3AED] hover:bg-[#6D28D9] text-white text-sm font-medium rounded-lg transition-colors">
                            DÃ©marrer une campagne
                        </button>

                        {/* Notifications */}
                        <div className="relative">
                            <button className="w-10 h-10 flex items-center justify-center rounded-lg hover:bg-[#2D1F43] transition-colors group">
                                <NotificationIcon />
                                <span className="absolute -top-1 -right-1 w-5 h-5 bg-[#DC2626] text-white text-xs font-medium rounded-full flex items-center justify-center">
                                    {prospects.length}
                                </span>
                            </button>
                        </div>

                        {/* Profil utilisateur */}
                        <div className="relative" ref={menuRef}>
                            <button
                                onClick={() => setShowMenu(!showMenu)}
                                className="flex items-center gap-3 p-2 hover:bg-[#2D1F43] rounded-lg transition-colors"
                            >
                                <img
                                    src={userData.avatar}
                                    alt="Profile"
                                    className="w-8 h-8 rounded-full object-cover"
                                    onError={(e) => {
                                        e.target.src = `https://ui-avatars.com/api/?name=${userData.firstName}+${userData.lastName}&background=random`;
                                    }}
                                />
                                <span className="text-sm font-medium text-white">
                                    {userData.firstName} {userData.lastName}
                                </span>
                                <svg
                                    className={`w-4 h-4 text-gray-400 transition-transform duration-200 ${
                                        showMenu ? 'rotate-180' : ''
                                    }`}
                                    viewBox="0 0 24 24"
                                    fill="none"
                                    stroke="currentColor"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                >
                                    <polyline points="6 9 12 15 18 9" />
                                </svg>
                            </button>

                            {/* Menu dÃ©roulant */}
                            {showMenu && (
                                <div className="absolute right-0 mt-2 w-56 bg-white rounded-lg shadow-lg py-1 z-50">
                                    <button
                                        onClick={() => setShowSettings(true)}
                                        className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 flex items-center gap-2"
                                    >
                                        âï¸ ParamÃ¨tres
                                    </button>
                                    <button
                                        onClick={() => {
                                            fetchUserData();
                                            fetchProspects();
                                        }}
                                        className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 flex items-center gap-2"
                                    >
                                        ð RafraÃ®chir
                                    </button>
                                    <hr className="my-1 border-gray-200" />
                                    <button
                                        onClick={handleLogout}
                                        className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-50 flex items-center gap-2"
                                    >
                                        ðª DÃ©connexion
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </header>

            {/* Contenu principal */}
            <main className="pt-[72px] px-6 max-w-[1800px] mx-auto">
                {/* Stats Section */}
                {visibleWidgets.statistics && (
                    <>
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
                            <StatCard
                                icon="ð¥"
                                value={prospects.length}
                                label="Prospects ajoutÃ©s"
                                color="#7C3AED"
                            />
                            <StatCard
                                icon="ð¨"
                                value={Math.round(prospects.length * 1.5)}
                                label="Invitations envoyÃ©es"
                                color="#8B5CF6"
                            />
                            <StatCard
                                icon="âï¸"
                                value={Math.round(prospects.length * 0.8)}
                                label="Messages envoyÃ©s"
                                color="#A78BFA"
                            />
                            <StatCard
                                icon="ð¤"
                                value={Math.round(prospects.length * 0.6)}
                                label="Connexions rÃ©alisÃ©es"
                                color="#C4B5FD"
                            />
                        </div>

                        <div className="bg-white p-6 rounded-lg shadow-sm mb-6">
                            <Line data={chartData} options={{
                                responsive: true,
                                plugins: {
                                    legend: {
                                        display: false
                                    }
                                },
                                scales: {
                                    y: {
                                        beginAtZero: true
                                    }
                                }
                            }} />
                        </div>
                    </>
                )}

                {/* Prospects Section */}
                {visibleWidgets.prospects && (
                    <div className="bg-white rounded-lg shadow-sm">
                        <div className="p-6 border-b border-gray-200">
                            <div className="flex justify-between items-center">
                                <h2 className="text-lg font-semibold text-gray-900">ð¥ Gestion des Prospects</h2>
                                <div className="flex items-center gap-4">
                                    <div className="relative">
                                        <input
                                            type="text"
                                            placeholder="Rechercher..."
                                            className="w-64 px-4 py-2 pr-8 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7C3AED] focus:border-transparent"
                                            value={searchTerm}
                                            onChange={handleSearchChange}
                                        />
                                        <svg
                                            className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400"
                                            width="16"
                                            height="16"
                                            viewBox="0 0 24 24"
                                            fill="none"
                                            stroke="currentColor"
                                            strokeWidth="2"
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                        >
                                            <circle cx="11" cy="11" r="8" />
                                            <path d="M21 21l-4.35-4.35" />
                                        </svg>
                                    </div>
                                    <button
                                        onClick={() => setShowImportModal(true)}
                                        className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                                    >
                                        Export
                                    </button>
                                    <button className="px-4 py-2 text-sm font-medium text-white bg-[#7C3AED] rounded-lg hover:bg-[#6D28D9] transition-colors">
                                        Add New
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="overflow-x-auto">
                            <table className="w-full">
                                <thead>
                                <tr className="text-left bg-gray-50">
                                    <th className="py-3 px-6">
                                        <input
                                            type="checkbox"
                                            className="w-4 h-4 rounded border-gray-300 text-[#7C3AED] focus:ring-[#7C3AED]"
                                            onChange={(e) => {
                                                if (e.target.checked) {
                                                    setSelectedProspects(prospects.map(p => p._id));
                                                } else {
                                                    setSelectedProspects([]);
                                                }
                                            }}
                                        />
                                    </th>
                                    <th className="py-3 px-6 text-sm font-medium text-gray-500">Client</th>
                                    <th className="py-3 px-6 text-sm font-medium text-gray-500">Email</th>
                                    <th className="py-3 px-6 text-sm font-medium text-gray-500">Source</th>
                                    <th className="py-3 px-6 text-sm font-medium text-gray-500">Status</th>
                                    <th className="py-3 px-6 text-sm font-medium text-gray-500 text-right">Actions</th>
                                </tr>
                                </thead>
                                <tbody>
                                {filteredProspects.map((prospect) => (
                                    <tr key={prospect._id} className="border-t border-gray-200 hover:bg-gray-50">
                                        <td className="py-4 px-6">
                                            <input
                                                type="checkbox"
                                                className="w-4 h-4 rounded border-gray-300 text-[#7C3AED] focus:ring-[#7C3AED]"
                                                checked={selectedProspects.includes(prospect._id)}
                                                onChange={() => handleSelectProspect(prospect._id)}
                                            />
                                        </td>
                                        <td className="py-4 px-6">
                                            <div className="flex items-center gap-3">
                                                <div className="flex-shrink-0">
                                                    <img
                                                        className="h-10 w-10 rounded-full"
                                                        src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                                        alt=""
                                                    />
                                                </div>
                                                <div className="flex flex-col">
                                                        <span className="text-sm font-medium text-gray-900">
                                                            {prospect.prenom} {prospect.nom}
                                                        </span>
                                                    <span className="text-sm text-gray-500">
                                                            ID: {prospect._id?.slice(-4)}
                                                        </span>
                                                </div>
                                            </div>
                                        </td>
                                        <td className="py-4 px-6 text-sm text-gray-500">
                                            {prospect.email || 'Non disponible'}
                                        </td>
                                        <td className="py-4 px-6 text-sm text-gray-500">
                                            {prospect.societe || 'Non disponible'}
                                        </td>
                                        <td className="py-4 px-6">
                                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                                    Active
                                                </span>
                                        </td>
                                        <td className="py-4 px-6 text-right">
                                            <button className="text-sm text-[#7C3AED] hover:text-[#6D28D9] font-medium">
                                                Edit
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>

                        {/* Pagination */}
                        <div className="px-6 py-4 flex items-center justify-between border-t border-gray-200">
                            <div className="flex items-center gap-2">
                                <span className="text-sm text-gray-700">
                                    Showing <span className="font-medium">1</span> to{" "}
                                    <span className="font-medium">{filteredProspects.length}</span> of{" "}
                                    <span className="font-medium">{prospects.length}</span> results
                                </span>
                            </div>
                            <div className="flex items-center gap-2">
                                <button className="px-3 py-1 text-sm text-gray-500 hover:text-[#7C3AED] disabled:opacity-50 disabled:cursor-not-allowed">
                                    Previous
                                </button>
                                <button className="px-3 py-1 text-sm bg-[#7C3AED] text-white rounded">
                                    1
                                </button>
                                <button className="px-3 py-1 text-sm text-gray-500 hover:text-[#7C3AED]">
                                    2
                                </button>
                                <button className="px-3 py-1 text-sm text-gray-500 hover:text-[#7C3AED]">
                                    3
                                </button>
                                <span className="text-gray-500">...</span>
                                <button className="px-3 py-1 text-sm text-gray-500 hover:text-[#7C3AED]">
                                    8
                                </button>
                                <button className="px-3 py-1 text-sm text-gray-500 hover:text-[#7C3AED]">
                                    Next
                                </button>
                            </div>
                        </div>
                    </div>
                )}

                {/* FAB */}
                <div className="fixed bottom-6 right-6">
                    <div className="relative">
                        <button
                            className="w-14 h-14 bg-[#7C3AED] rounded-full flex items-center justify-center text-white shadow-lg hover:bg-[#6D28D9] transition-all hover:scale-110"
                            onClick={() => {
                                const menu = document.querySelector('.widget-menu');
                                menu.classList.toggle('hidden');
                            }}
                        >
                            <svg
                                className="w-6 h-6"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                            >
                                <path
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                    strokeWidth="2"
                                    d="M12 6v6m0 0v6m0-6h6m-6 0H6"
                                />
                            </svg>
                        </button>
                        <div className="widget-menu hidden absolute bottom-16 right-0 bg-white rounded-lg shadow-lg w-48 overflow-hidden">
                            {Object.entries(visibleWidgets).map(([key, value]) => (
                                <button
                                    key={key}
                                    onClick={() => setVisibleWidgets(prev => ({
                                        ...prev,
                                        [key]: !value
                                    }))}
                                    className={`w-full text-left px-4 py-2 hover:bg-gray-50 flex justify-between items-center ${
                                        value ? 'text-[#7C3AED] bg-purple-50' : 'text-gray-700'
                                    }`}
                                >
                                    {key.charAt(0).toUpperCase() + key.slice(1)}
                                    <span>{value ? 'â' : '+'}</span>
                                </button>
                            ))}
                        </div>
                    </div>
                </div>
            </main>

            {/* Import Modal */}
            {showImportModal && (
                <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
                    <div className="bg-white rounded-lg p-6 w-full max-w-md">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="text-lg font-semibold text-gray-900">Import Prospects</h3>
                            <button
                                onClick={() => setShowImportModal(false)}
                                className="text-gray-400 hover:text-gray-500"
                            >
                                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                                </svg>
                            </button>
                        </div>
                        <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center mb-4">
                            <input
                                type="file"
                                accept=".csv"
                                onChange={(e) => {
                                    if (e.target.files[0]) {
                                        handleImport(e.target.files[0]);
                                    }
                                }}
                                className="hidden"
                                id="file-upload"
                            />
                            <label
                                htmlFor="file-upload"
                                className="cursor-pointer text-[#7C3AED] hover:text-[#6D28D9]"
                            >
                                Click to select CSV file
                            </label>
                            <p className="text-sm text-gray-500 mt-2">
                                or drag and drop your file here
                            </p>
                        </div>
                        <div className="flex justify-end gap-2">
                            <button
                                onClick={() => setShowImportModal(false)}
                                className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200"
                            >
                                Cancel
                            </button>
                            <button className="px-4 py-2 text-sm font-medium text-white bg-[#7C3AED] rounded-lg hover:bg-[#6D28D9]">
                                Import
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Settings Modal */}
            {showSettings && (
                <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
                    <div className="bg-white rounded-lg p-6 w-full max-w-lg">
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-lg font-semibold text-gray-900">Settings</h3>
                            <button
                                onClick={() => setShowSettings(false)}
                                className="text-gray-400 hover:text-gray-500"
                            >
                                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                                </svg>
                            </button>
                        </div>

                        <div className="space-y-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Profile Photo
                                </label>
                                <div className="flex items-center gap-4">
                                    <img
                                        src={userData.avatar}
                                        alt="Profile"
                                        className="w-16 h-16 rounded-full object-cover"
                                        onError={(e) => {
                                            e.target.src = `https://ui-avatars.com/api/?name=${userData.firstName}+${userData.lastName}&background=random`;
                                        }}
                                    />
                                    <button className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200">
                                        Change Photo
                                    </button>
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    First Name
                                </label>
                                <input
                                    type="text"
                                    value={userData.firstName}
                                    className="w-full px-4 py-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7C3AED] focus:border-transparent"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Last Name
                                </label>
                                <input
                                    type="text"
                                    value={userData.lastName}
                                    className="w-full px-4 py-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7C3AED] focus:border-transparent"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Email
                                </label>
                                <input
                                    type="email"
                                    value={userData.email}
                                    className="w-full px-4 py-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7C3AED] focus:border-transparent"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    Password
                                </label>
                                <input
                                    type="password"
                                    placeholder="â¢â¢â¢â¢â¢â¢â¢â¢"
                                    className="w-full px-4 py-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#7C3AED] focus:border-transparent"
                                />
                                <p className="mt-1 text-sm text-gray-500">
                                    Leave blank to keep current password
                                </p>
                            </div>
                        </div>

                        <div className="flex justify-end gap-2 mt-6">
                            <button
                                onClick={() => setShowSettings(false)}
                                className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                className="px-4 py-2 text-sm font-medium text-white bg-[#7C3AED] hover:bg-[#6D28D9] rounded-lg transition-colors"
                            >
                                Save Changes
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Dashboard;
