import React, { useState, useEffect } from 'react';
import CSVReader from 'react-csv-reader';
import axios from 'axios';
import './ProspectListWidget.css';

const ProspectListWidget = ({ prospects, onProspectsUpdate }) => {
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportModal, setShowImportModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [displayedProspects, setDisplayedProspects] = useState([]);
    const [itemsPerPage] = useState(5);

    const loadMoreProspects = () => {
        const startIndex = displayedProspects.length;
        const filteredProspects = prospects.filter(prospect =>
            prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
        );
        const newProspects = filteredProspects.slice(startIndex, startIndex + itemsPerPage);
        setDisplayedProspects(prev => [...prev, ...newProspects]);
    };

    useEffect(() => {
        // Charger les prospects filtrés lors de la première exécution ou lors du changement du terme de recherche
        setDisplayedProspects(prospects.filter(prospect =>
            prospect.nom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.prenom?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            prospect.societe?.toLowerCase().includes(searchTerm.toLowerCase())
        ).slice(0, itemsPerPage));
    }, [prospects, searchTerm]);

    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + window.scrollY >= document.documentElement.scrollHeight - 50) {
                loadMoreProspects();
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [displayedProspects, prospects, searchTerm]);

    const handleFileUpload = async (data) => {
        // Votre code pour gérer l'importation
    };

    const handleDeleteSelected = async () => {
        // Votre code pour supprimer les prospects sélectionnés
    };

    const handleSelectProspect = (id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(prospectId => prospectId !== id) : [...prev, id]
        );
    };

    const handleSelectAll = () => {
        const currentProspectIds = displayedProspects.map(prospect => prospect._id);
        if (currentProspectIds.every(id => selectedProspects.includes(id))) {
            setSelectedProspects(prev => prev.filter(id => !currentProspectIds.includes(id)));
        } else {
            setSelectedProspects(prev => [...new Set([...prev, ...currentProspectIds])]);
        }
    };

    return (
        <div className="prospect-list-widget">
            <div className="prospect-actions">
                <button onClick={() => setShowImportModal(true)} className="btn import-btn">
                    Importer des prospects
                </button>
                <button
                    onClick={handleDeleteSelected}
                    className={`btn delete-btn ${selectedProspects.length > 0 ? 'enabled' : 'disabled'}`}
                    disabled={loading || selectedProspects.length === 0}
                >
                    Supprimer les prospects sélectionnés
                </button>
                <button onClick={handleSelectAll} className="select-all-btn">
                    {displayedProspects.every(prospect => selectedProspects.includes(prospect._id)) ? "Tout désélectionner" : "Sélectionner tout"}
                </button>
                <input
                    type="text"
                    className="search-input"
                    placeholder="Rechercher..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            <div className={`prospect-content ${showImportModal ? 'dimmed' : ''}`}>
                {loading ? (
                    <div className="loading">Chargement...</div>
                ) : (
                    <div className="prospect-table-container">
                        <table className="prospect-table">
                            <thead>
                            <tr>
                                <th>Sélection</th>
                                <th>Photo</th>
                                <th>Nom</th>
                                <th>Email</th>
                                <th>Société</th>
                                <th>LinkedIn</th>
                            </tr>
                            </thead>
                            <tbody>
                            {displayedProspects.map((prospect) => (
                                <tr key={prospect._id}>
                                    <td>
                                        <input
                                            type="checkbox"
                                            checked={selectedProspects.includes(prospect._id)}
                                            onChange={() => handleSelectProspect(prospect._id)}
                                        />
                                    </td>
                                    <td>
                                        <img
                                            src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                            alt={`${prospect.prenom} ${prospect.nom}`}
                                            className="prospect-avatar"
                                        />
                                    </td>
                                    <td>{prospect.prenom} {prospect.nom}</td>
                                    <td>{prospect.email || 'Non disponible'}</td>
                                    <td>{prospect.societe || 'Non disponible'}</td>
                                    <td>
                                        <a
                                            href={prospect.linkedin}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="linkedin-btn"
                                        >
                                            profil
                                        </a>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>

            {showImportModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Importer des prospects</h3>
                        <CSVReader
                            onFileLoaded={handleFileUpload}
                            cssClass="csv-reader-input"
                            label="Importer une liste CSV"
                            parserOptions={{ header: false }}
                        />
                        <button onClick={() => setShowImportModal(false)} className="btn close-btn">
                            Fermer
                        </button>
                        {error && <p className="error-message">{error}</p>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProspectListWidget;
