import React, { useState, useEffect, useCallback, useMemo } from 'react';
import CSVReader from 'react-csv-reader';
import axios from 'axios';
import debounce from 'lodash/debounce';
import './ProspectListWidget.css';

const API_BASE_URL = 'http://localhost:5001/api';
const ITEMS_PER_PAGE = 5;

const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json'
    }
});

api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

const ProspectListWidget = ({ prospects: initialProspects, onProspectsUpdate }) => {
    const [selectedProspects, setSelectedProspects] = useState([]);
    const [showImportModal, setShowImportModal] = useState(false);
    const [showCreateListModal, setShowCreateListModal] = useState(false);
    const [showEditListModal, setShowEditListModal] = useState(false);
    const [showAddToListModal, setShowAddToListModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [displayedProspects, setDisplayedProspects] = useState([]);
    const [allProspects, setAllProspects] = useState(initialProspects || []);
    const [lists, setLists] = useState([]);
    const [selectedList, setSelectedList] = useState(null);
    const [currentList, setCurrentList] = useState(null);
    const [newListName, setNewListName] = useState('');
    const [editListName, setEditListName] = useState('');
    const [listSearchTerm, setListSearchTerm] = useState('');

    const debouncedSearch = useCallback(
        debounce((value) => {
            setSearchTerm(value);
        }, 300),
        []
    );

    const handleError = useCallback((error, message) => {
        console.error(message, error);
        setError(error.response?.data?.message || message);
        setTimeout(() => setError(''), 3000);
    }, []);

    const fetchLists = useCallback(async () => {
        try {
            const response = await api.get('/lists');
            if (response.data.success) {
                console.log("Listes récupérées:", response.data.lists);
                setLists(response.data.lists);
            }
        } catch (error) {
            handleError(error, "Erreur lors du chargement des listes de prospects");
        }
    }, [handleError]);

    const fetchProspects = useCallback(async () => {
        try {
            console.log("Fetching prospects for list:", selectedList);
            setLoading(true);
            let response;

            if (selectedList) {
                response = await api.get(`/lists/${selectedList}`);
                console.log("List response:", response.data);

                if (response.data.success && response.data.list && Array.isArray(response.data.list.prospects)) {
                    // S'assurer que nous avons un tableau de prospects
                    const prospects = response.data.list.prospects;
                    console.log("List prospects:", prospects);

                    // Vérifier et formater chaque prospect
                    const formattedProspects = prospects.map(prospect => {
                        if (!prospect) return null;
                        return {
                            _id: prospect._id || prospect.id,
                            prenom: prospect.prenom || 'Non disponible',
                            nom: prospect.nom || 'Non disponible',
                            email: prospect.email || 'Non disponible',
                            societe: prospect.societe || 'Non disponible',
                            linkedin: prospect.linkedin || '#'
                        };
                    }).filter(Boolean); // Filtrer les prospects null

                    console.log("Formatted prospects:", formattedProspects);
                    setAllProspects(formattedProspects);
                    setDisplayedProspects(formattedProspects.slice(0, ITEMS_PER_PAGE));
                    onProspectsUpdate(formattedProspects);
                } else {
                    console.log("No prospects in list or invalid response");
                    setAllProspects([]);
                    setDisplayedProspects([]);
                    onProspectsUpdate([]);
                }
            } else {
                // Récupérer tous les prospects
                console.log("Fetching all prospects");
                response = await api.get('/prospects');

                if (response.data.success && Array.isArray(response.data.prospects)) {
                    const prospects = response.data.prospects;
                    console.log("All prospects:", prospects);

                    setAllProspects(prospects);
                    setDisplayedProspects(prospects.slice(0, ITEMS_PER_PAGE));
                    onProspectsUpdate(prospects);
                }
            }
        } catch (error) {
            console.error("Error fetching prospects:", error);
            handleError(error, "Erreur lors du chargement des prospects");
            setAllProspects([]);
            setDisplayedProspects([]);
        } finally {
            setLoading(false);
        }
    }, [selectedList, handleError, onProspectsUpdate]);
    const filteredProspects = useMemo(() => {
        if (!searchTerm) return allProspects;

        const searchTermLower = searchTerm.toLowerCase();
        return allProspects.filter(prospect => {
            if (!prospect) return false;
            const searchFields = [
                prospect.nom,
                prospect.prenom,
                prospect.email,
                prospect.societe
            ].map(field => String(field || '').toLowerCase());

            return searchFields.some(field => field.includes(searchTermLower));
        });
    }, [allProspects, searchTerm]);

    useEffect(() => {
        if (filteredProspects.length > 0) {
            setDisplayedProspects(filteredProspects.slice(0, ITEMS_PER_PAGE));
        }
    }, [filteredProspects]);

    useEffect(() => {
        console.log("Selected list changed, fetching prospects...");
        fetchProspects();
    }, [selectedList, fetchProspects]);

    useEffect(() => {
        fetchLists();
    }, [fetchLists]);

    const handleFileUpload = async (data) => {
        try {
            setLoading(true);
            const formattedProspects = data.map(row => ({
                prenom: row[0] || 'Non disponible',
                nom: row[1] || 'Non disponible',
                email: row[2] || 'Non disponible',
                societe: row[3] || 'Non disponible',
                linkedin: row[4] || 'Non disponible'
            }));

            await Promise.all(
                formattedProspects.map(prospect =>
                    api.post('/prospects/add', prospect)
                )
            );

            await fetchProspects();
            setShowImportModal(false);
        } catch (error) {
            handleError(error, "Erreur lors de l'import des prospects");
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteSelected = async () => {
        try {
            setLoading(true);
            await Promise.all(
                selectedProspects.map(id =>
                    api.delete(`/prospects/${id}`)
                )
            );

            await fetchProspects();
            setSelectedProspects([]);
        } catch (error) {
            handleError(error, "Erreur lors de la suppression des prospects");
        } finally {
            setLoading(false);
        }
    };

    const createNewList = async (e) => {
        e.preventDefault();
        if (newListName.trim() === '') return;

        try {
            setLoading(true);
            const response = await api.post('/lists', { name: newListName });
            if (response.data.success) {
                await fetchLists();
                setNewListName('');
                setShowCreateListModal(false);
                setError('');
            }
        } catch (error) {
            handleError(error, "Erreur lors de la création de la liste");
        } finally {
            setLoading(false);
        }
    };

    const handleEditList = async (e) => {
        e.preventDefault();
        if (!editListName.trim() || !currentList) return;

        try {
            setLoading(true);
            const response = await api.put(`/lists/${currentList._id}`, {
                name: editListName
            });

            if (response.data.success) {
                await fetchLists();
                setShowEditListModal(false);
                setEditListName('');
                setCurrentList(null);
            }
        } catch (error) {
            handleError(error, "Erreur lors de la modification de la liste");
        } finally {
            setLoading(false);
        }
    };

    const handleDeleteList = async (listId) => {
        if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette liste ?')) return;

        try {
            setLoading(true);
            const response = await api.delete(`/lists/${listId}`);

            if (response.data.success) {
                await fetchLists();
                if (selectedList === listId) {
                    setSelectedList(null);
                }
            }
        } catch (error) {
            handleError(error, "Erreur lors de la suppression de la liste");
        } finally {
            setLoading(false);
        }
    };

    const handleListClick = useCallback((listId) => {
        console.log("Clicking list:", listId);
        setSelectedProspects([]); // Réinitialiser la sélection
        setSearchTerm(''); // Réinitialiser la recherche
        setSelectedList(listId); // Mettre à jour la liste sélectionnée
    }, []);

    const handleAddProspectsToList = async (listId) => {
        try {
            setLoading(true);
            const response = await api.post(`/lists/${listId}/prospects`, {
                prospectIds: selectedProspects
            });

            if (response.data.success) {
                await fetchLists();
                setShowAddToListModal(false);
                setSelectedProspects([]);
                if (selectedList === listId) {
                    await fetchProspects();
                }
            }
        } catch (error) {
            handleError(error, "Erreur lors de l'ajout des prospects à la liste");
        } finally {
            setLoading(false);
        }
    };

    const loadMoreProspects = useCallback(() => {
        const startIndex = displayedProspects.length;
        const endIndex = startIndex + ITEMS_PER_PAGE;
        const newProspects = filteredProspects.slice(startIndex, endIndex);
        if (newProspects.length > 0) {
            setDisplayedProspects(prev => [...prev, ...newProspects]);
        }
    }, [displayedProspects.length, filteredProspects]);

    const handleScroll = useCallback(() => {
        const scrollHeight = document.documentElement.scrollHeight;
        const scrollTop = window.scrollY;
        const clientHeight = window.innerHeight;

        if (scrollTop + clientHeight >= scrollHeight - 50) {
            loadMoreProspects();
        }
    }, [loadMoreProspects]);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [handleScroll]);

    const handleSelectProspect = useCallback((id) => {
        setSelectedProspects(prev =>
            prev.includes(id) ? prev.filter(prospectId => prospectId !== id) : [...prev, id]
        );
    }, []);

    const handleSelectAll = useCallback(() => {
        const currentProspectIds = displayedProspects.map(prospect => prospect._id);
        setSelectedProspects(prev =>
            prev.length === currentProspectIds.length ? [] : [...new Set([...prev, ...currentProspectIds])]
        );
    }, [displayedProspects]);

    const filteredLists = useMemo(() =>
            lists.filter(list =>
                list.name.toLowerCase().includes(listSearchTerm.toLowerCase())
            ),
        [lists, listSearchTerm]);

    return (
        <div className="prospect-list-widget">
            <div className="list-sidebar">
                <h3>Listes de Prospects</h3>
                <input
                    type="text"
                    placeholder="Rechercher une liste..."
                    value={listSearchTerm}
                    onChange={(e) => setListSearchTerm(e.target.value)}
                    className="list-search-input"
                />
                <ul className="list-names">
                    <li
                        onClick={() => handleListClick(null)}
                        className={`list-item ${!selectedList ? 'active' : ''}`}
                    >
                        <div className="list-item-content">
                            <span className="list-name">Tous les prospects</span>
                            <span className="list-count">({allProspects.length})</span>
                        </div>
                    </li>
                    {filteredLists.map(list => (
                        <li
                            key={list._id}
                            className={`list-item ${selectedList === list._id ? 'active' : ''}`}
                        >
                            <div
                                className="list-item-content"
                                onClick={() => handleListClick(list._id)}
                            >
                                <span className="list-name">{list.name}</span>
                                <span className="list-count">
                                    ({list.prospectsCount || 0})
                                </span>
                            </div>
                            <div className="list-actions">
                                <button
                                    className="icon-button edit"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        setCurrentList(list);
                                        setEditListName(list.name);
                                        setShowEditListModal(true);
                                    }}
                                >
                                    ✏️
                                </button>
                                <button
                                    className="icon-button delete"
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        handleDeleteList(list._id);
                                    }}
                                >
                                    🗑️
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
                <button
                    onClick={() => setShowCreateListModal(true)}
                    className="btn add-list-btn"
                >
                    Créer une liste
                </button>
            </div>

            <div className="main-content">
                <div className="prospect-actions">
                    <button onClick={() => setShowImportModal(true)} className="btn import-btn">
                        Importer des prospects
                    </button>
                    <button
                        onClick={handleDeleteSelected}
                        className={`btn delete-btn ${selectedProspects.length > 0 ? 'enabled' : 'disabled'}`}
                        disabled={loading || selectedProspects.length === 0}
                    >
                        Supprimer les prospects sélectionnés
                    </button>
                    {selectedProspects.length > 0 && (
                        <button
                            onClick={() => setShowAddToListModal(true)}
                            className="btn add-to-list-btn"
                        >
                            Ajouter à une liste
                        </button>
                    )}
                    <div className="search-container">
                        <input
                            type="text"
                            className="search-input"
                            placeholder="Rechercher un prospect..."
                            onChange={(e) => debouncedSearch(e.target.value)}
                        />
                        {searchTerm && (
                            <button
                                className="clear-search"
                                onClick={() => setSearchTerm('')}
                            >
                                ✕
                            </button>
                        )}
                    </div>
                </div>

                <div className={`prospect-content ${showImportModal ? 'dimmed' : ''}`}>
                    {loading ? (
                        <div className="loading">Chargement...</div>
                    ) : (
                        <div className="prospect-table-container">
                            <table className="prospect-table">
                                <thead>
                                <tr>
                                    <th>
                                        <input
                                            type="checkbox"
                                            checked={displayedProspects.length > 0 &&
                                                displayedProspects.every(prospect =>
                                                    selectedProspects.includes(prospect._id)
                                                )}
                                            onChange={handleSelectAll}
                                        />
                                    </th>
                                    <th>Photo</th>
                                    <th>Nom</th>
                                    <th>Email</th>
                                    <th>Société</th>
                                    <th>LinkedIn</th>
                                </tr>
                                </thead>
                                <tbody>
                                {displayedProspects.length === 0 ? (
                                    <tr>
                                        <td colSpan="6" className="no-results">
                                            Aucun prospect trouvé
                                        </td>
                                    </tr>
                                ) : (
                                    displayedProspects.map((prospect) => (
                                        <tr key={prospect._id}>
                                            <td>
                                                <input
                                                    type="checkbox"
                                                    checked={selectedProspects.includes(prospect._id)}
                                                    onChange={() => handleSelectProspect(prospect._id)}
                                                />
                                            </td>
                                            <td>
                                                <img
                                                    src={`https://ui-avatars.com/api/?name=${prospect.prenom}+${prospect.nom}&background=random`}
                                                    alt={`${prospect.prenom} ${prospect.nom}`}
                                                    className="prospect-avatar"
                                                />
                                            </td>
                                            <td>{prospect.prenom} {prospect.nom}</td>
                                            <td>{prospect.email || 'Non disponible'}</td>
                                            <td>{prospect.societe || 'Non disponible'}</td>
                                            <td>
                                                <a
                                                    href={prospect.linkedin}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    className="linkedin-btn"
                                                >
                                                    Voir le profil
                                                </a>
                                            </td>
                                        </tr>
                                    ))
                                )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {/* Modaux */}
            {showCreateListModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Créer une nouvelle liste</h3>
                        <form onSubmit={createNewList}>
                            <div className="modal-body">
                                <input
                                    type="text"
                                    placeholder="Nom de la liste"
                                    value={newListName}
                                    onChange={(e) => setNewListName(e.target.value)}
                                    className="new-list-input"
                                    autoFocus
                                />
                                {error && <p className="error-message">{error}</p>}
                            </div>
                            <div className="modal-actions">
                                <button
                                    type="submit"
                                    className="btn save-btn"
                                    disabled={!newListName.trim() || loading}
                                >
                                    {loading ? 'Création...' : 'Créer'}
                                </button>
                                <button
                                    type="button"
                                    onClick={() => {
                                        setShowCreateListModal(false);
                                        setNewListName('');
                                        setError('');
                                    }}
                                    className="btn cancel-btn"
                                >
                                    Annuler
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {showEditListModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Modifier la liste</h3>
                        <form onSubmit={handleEditList}>
                            <div className="modal-body">
                                <input
                                    type="text"
                                    placeholder="Nom de la liste"
                                    value={editListName}
                                    onChange={(e) => setEditListName(e.target.value)}
                                    className="edit-list-input"autoFocus
                                />
                                {error && <p className="error-message">{error}</p>}
                            </div>
                            <div className="modal-actions">
                                <button
                                    type="submit"
                                    className="btn save-btn"
                                    disabled={!editListName.trim() || loading}
                                >
                                    {loading ? 'Modification...' : 'Modifier'}
                                </button>
                                <button
                                    type="button"
                                    onClick={() => {
                                        setShowEditListModal(false);
                                        setEditListName('');
                                        setCurrentList(null);
                                        setError('');
                                    }}
                                    className="btn cancel-btn"
                                >
                                    Annuler
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {showAddToListModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Ajouter à une liste</h3>
                        <div className="modal-body">
                            <div className="lists-selection">
                                {filteredLists.map(list => (
                                    <button
                                        key={list._id}
                                        onClick={() => handleAddProspectsToList(list._id)}
                                        className="list-selection-btn"
                                        disabled={loading}
                                    >
                                        {list.name}
                                    </button>
                                ))}
                            </div>
                            {error && <p className="error-message">{error}</p>}
                        </div>
                        <div className="modal-actions">
                            <button
                                onClick={() => setShowAddToListModal(false)}
                                className="btn cancel-btn"
                                disabled={loading}
                            >
                                Fermer
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {showImportModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h3>Importer des prospects</h3>
                        <CSVReader
                            onFileLoaded={handleFileUpload}
                            cssClass="csv-reader-input"
                            label="Importer une liste CSV"
                            parserOptions={{ header: false }}
                        />
                        <div className="modal-actions">
                            <button
                                onClick={() => setShowImportModal(false)}
                                className="btn cancel-btn"
                                disabled={loading}
                            >
                                Fermer
                            </button>
                        </div>
                        {error && <p className="error-message">{error}</p>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProspectListWidget;
